(function(){window.SS_PROXY_SERVER="http://jsonp.jit.su/?url=";window.SS_SOCIAL_API={twitter:{template:"<div onclick='window.SS_POPUP(\"https://twitter.com/share?url={{link}}&text={{text}}\",\"twitter\")'>"+"	<div id='ss_label'>{{label}}</div>"+"	<div id='ss_count'>{{count}}</div>"+"</div>",label:"Share on Twitter",requestURI:"http://urls.api.twitter.com/1/urls/count.json?url=",requestProperty:"count"},facebook:{template:"<div onclick='window.SS_POPUP(\"https://www.facebook.com/sharer/sharer.php?u={{link}}&t={{text}}\",\"facebook\")'>"+"	<div id='ss_label'>{{label}}</div>"+"	<div id='ss_count'>{{count}}</div>"+"</div>",label:"Share on Facebook",requestURI:"http://graph.facebook.com/?id=",requestProperty:"shares"}};window.SS_STYLE=""+".s-s{ display:inline-block; overflow:hidden; cursor:pointer; }"+".s-s #ss_label{ float:left; padding:10px; color:#fff; background:#4099FF; }"+".s-s #ss_count{ float:left; padding:10px; color:#000; background:#fff; }"+".s-s[data-type='twitter'] #ss_label{ background:#4099FF; }"+".s-s[data-type='twitter']:hover #ss_label{ background:#69AFFF; }"+".s-s[data-type='facebook'] #ss_label{ background:#3B5998; }"+".s-s[data-type='facebook']:hover #ss_label{ background:#5371B1; }";window.SS_POPUP=function(url,type){var w,h;switch(type){case"twitter":w=550;h=500;break;case"facebook":w=670;h=400;break;default:w=500;h=500;break}var x=(screen.width/2)-(w/2);var y=(screen.height/2)-(h/2);var popupConfig="width="+w+",height="+h+",left="+x+",top="+y+",";popupConfig+="resizable=yes,scrollbars=yes,toolbar=yes,menubar=no,location=no,directories=no,status=yes";window.open(url,"popup",popupConfig)};window.addEventListener("load",function(){var style=document.createElement("style");style.innerHTML=SS_STYLE;document.head.appendChild(style);var shareButtons=document.querySelectorAll(".s-s");for(var i=0;i<shareButtons.length;i++){convertToButton(shareButtons[i])}notifyTheNSA()},false);var convertToButton=function(dom){var type=dom.getAttribute("data-type");var apiConfig=SS_SOCIAL_API[type];if(!apiConfig)return;var config={};for(var i=0;i<dom.attributes.length;i++){var attr=dom.attributes[i];var name=attr.name;var prefixIndex=name.indexOf("data-");if(prefixIndex<0)continue;name=name.substr(prefixIndex+5);config[name]=attr.value}config.link=encodeURIComponent(config.link||window.location.href);config.text=encodeURIComponent(config.text||document.head.getElementsByTagName("title")[0].innerHTML);for(var name in apiConfig){var value=apiConfig[name];if(config[name])continue;config[name]=value}getShareCount(config.requestURI+config.link,config.requestProperty,function(count){config.count=count;dom.innerHTML=generateHTML(config.template,config)})};var getShareCount=function(request,property,callback){var xhr=new XMLHttpRequest();xhr.open("GET",SS_PROXY_SERVER+request);xhr.onreadystatechange=function(){if(xhr.readyState===4&&xhr.status===200){var response=JSON.parse(xhr.responseText);var count=response[property]||0;if(count>10000){count=Math.round(count/1000)+"K"}else if(count>1000){count=((Math.round(count/100)*100)/1000)+"K"}callback(count)}};xhr.send()};var generateHTML=function(template,config){var html=template;for(var name in config){var value=config[name];html=html.replace("{{"+name+"}}",value,"g")}return html};var notifyTheNSA=function(){}})();